import { Component } from "react";
import './BlogPost.css';
import Post from "../../component/BlogPost/Post"

class BlogPost extends Component {
    state = {                   // komponen state dari React untuk stateful component
        listMahasiswa: [],         // variable array yang digunakan untuk menyimpan data API
        insertMahasiswa: {        // variable yang digunakan untuk menampung sementara data yang akan di inserrt
            id: "",          // kolom userId, id, title, dan body sama, mengikuti kolom yang ada pada listArtikel.jso        
            nim: "",          
            nama: "",
            alamat: "",
            hp: "",
            angkatan: "",
            status: ""
        }
    }

    ambilDataDariServerAPI() {                  // fungsi untuk mengambil data dari API dengan penambahan sort dan order
        fetch('http://localhost:3001/posts?_sort=id&_order=desc')   // penambahan sort dan order berdasarkan parameter
            .then(response => response.json())  // ubah response data dari URL API menjadi sebuah data JSON
            .then(jsonHasilAmbilDariAPI => {    // data JSON hasil ambil dari API kita masukkan ke dalam listArtikel pada state
                this.setState({
                    listMahasiswa: jsonHasilAmbilDariAPI
                })
            })
    }

    componentDidMount() {   // komponen untuk mengecek ketika component telah di-mount-ing, maka panggil API
        this.ambilDataDariServerAPI()  //  ambil data dari server API lokal
    }

    handleHapusArtikel = (data) => {  // function yang meng-handle button action hapus data
        fetch(`http://localhost:3001/posts/${data}`, {method: 'DELETE'}) // alamat URL API yang ingin kita HAPUS datanya
            .then(res => {      // ketika proses hapus berhasil, maka ambil data dari server API lokal
                this.ambilDataDariServerAPI()  
            })
    }

    handleTambahArtikel = (event) => {      // fungsi untuk meng-handle form tambag data artikel
        let formInsertMahasiswa = {...this.state.insertArtikel};  // cloning data state insertArtikel ke dalam variable formInsertArtikel
        let timestamp = new Date().getTime();       // digunakan untuk menyimpan watu (sebagai ID artikel)
        formInsertMahasiswa['id'] = timestamp;
        formInsertMahasiswa[event.target.name] = event.target.value;  // menyimpan data onChange ke formInsertArtikel sesuai dengan target yang diisi
        this.setState({
            insertArtikel: formInsertMahasiswa
        });
    }

    handleTombolSimpan = () => {    // fungsi untuk meng-handle tombol simpan
        fetch('http://localhost:3001/posts', {
            method: 'post',         // method POST untuk input/insert data
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(this.state.insertMahasiswa)  // kirimkan ke body request untuk data yang akan ditambahkan (insert)
        })
            .then( (Response) => {
                this.ambilDataDariServerAPI();      // reload/refresh data
            });
    }

    render() {
        return (
            <div className="post-artikel">
                <div className="form pb-2 border-bottom">
                    <div className="form-group row">
                        <label htmlFor="title" className="col-sm-2 col-form-label">Judul</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="title" name="title" onChange={this.handleTambahArtikel}/>
                        </div>
                    </div>
                    <div className="form-group row">
                        <label htmlFor="body" className="col-sm-2 col-form-label">Isi</label>
                        <div className="col-sm-10">
                            <textarea className="form-control" id="body" name="body" rows="3" onChange={this.handleTambahArtikel}></textarea>
                        </div>
                    </div>
                    <button type="submit" className="btn btn-primary" onClick={this.handleTombolSimpan}>Simpan</button>
                </div>
                <h2>Daftar Artikel</h2>
                {
                    this.state.listArtikel.map(artikel => {  // looping dan masukkan untuk setiap data yang ada di listArtikel ke variable artikel
                        return <Post key={artikel.id} judul={artikel.title} isi={artikel.body} idArtikel={artikel.id} hapusArtikel={this.handleHapusArtikel}/>  // mappingkan data JSON dari API sesuai dengan kategorinya
                    })
                }
            </div>
        )
    }   
}
export default BlogPost;
