import React, {Component} from "react";
import './BlogPost.css';
import Post from "../../component/BlogPost/Post"

class BlogPost extends Component {
    state = {                   // komponen state dari React untuk stateful component
        listArtikel: []         // variable array yang digunakan untuk menyimpan data API
    }
    
    
    ambilDataDariServerAPI() {                  // fungsi untuk mengambil data dari API dengan penambahan sort dan order
        fetch('http://localhost:3001/posts?_sort=id&_order=desc')   // penambahan sort dan order berdasarkan parameter
            .then(response => response.json())  // ubah response data dari URL API menjadi sebuah data JSON
            .then(jsonHasilAmbilDariAPI => {    // data JSON hasil ambil dari API kita masukkan ke dalam listArtikel pada state
                this.setState({
                    listArtikel: jsonHasilAmbilDariAPI
                })
            })
    }

    componentDidMount() {   // komponen untuk mengecek ketika component telah di-mount-ing, maka panggil API
        this.ambilDataDariServerAPI()  //  ambil data dari server API lokal
    }

    render(){
        return(
            <div className="post-artikel">
                <h2>Daftar Artikel</h2>
                {
                    this.state.listArtikel.map(artikel => { // looping dan masukkan untuk setiap data yang ada di litsArtikel ke variabel artikel
                        return <Post key={artikel.id} judul={artikel.title} isi={artikel.body}/> // mappingkan data json dari API sesuai dengan kategorinya
                    })
                }
            </div>
        )
    }
}


export default BlogPost;
